import Navbar from "./components/Navbar";
import Home from "./components/Home.jsx";
import Blogs from "./components/Pages/Blogs/Blogs.jsx";


import Netart from "./components/Netart.jsx";
import Footer from "./components/Footer.jsx";
import Styleguide from "./components/Styleguide.jsx";
import { Routes } from 'react-router-dom';
import { Route } from 'react-router-dom';
import React,{ Component } from "react";
import { Link } from "react-router-dom";
import ColorItem from './color-item';

import "./styles.css";


class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      bgColor: "#2d3436",
    };
  }

  setTheme = (color) => {
    this.setState({ bgColor: color });
  };

  setColor = (color) => {
    this.setTheme(color);
    console.log(color);
  };

  render(){
    const colors = ['#2d3436' , '#4834d4' , '#be2edd' , '#f9ca24' , '#6ab04c' , '#30336b'];

    const setTheme = (color) =>{
      document.documentElement.style.setProperty('--bg-color' , color)
    }

    const setColor = (event) => {
      const currentColor = window.getComputedStyle(document.documentElement).getPropertyValue('--bg-color');
      setTheme(currentColor);
      console.log(currentColor);
    }

  return (
<div className="App" style={{ backgroundColor: this.state.bgColor }}>
        <button onClick={() => this.setColor(colors[0])}>Color 1</button>
        <button onClick={() => this.setColor(colors[1])}>Color 2</button>

    <div className="App">
      <nav>
          <div className="nav">
            <div>
              <ul id= "navbar">
          <Link to="/"><li>Home</li></Link>
          <Link to="/Blogs"><li>Blog</li></Link>
          <Link to="/Netart"><li>NetArt</li></Link>
          <Link to="/Styleguide"><li>Design</li></Link>
              </ul>
            </div>
            </div>

        </nav>
        <div className="color-switcher">
          <button><i class="ri-settings-2-fill"></i></button>
          <h2 className="headingg">Select Color</h2>
        <div className="color-list"></div>
        {colors.map((color , idx) => <ColorItem setColor={setColor}color = {color}/> )}
        </div>
      <Routes>

        <Route  path='/' element={<Home/>}/>
        <Route  path='/Blogs' element={<Blogs/>} />
        <Route  path="/Netart" element = {<Netart/>}/>
        <Route  path="/Styleguide" element = {<Styleguide/>}/>
        </Routes>




        <Footer/>

    </div>
    </div>
  );
}
}

export default App;
