import "./HomeStyles.css";
import React, { useState, useRef, useEffect } from "react";



export function Netart() {

  const canvasRef = useRef(null);
  const bodyRef = useRef(null);
  const theInputRef = useRef(null);
  const ageInputRef = useRef(null);
  const ageOutputRef = useRef(null);
  const [theColor, setTheColor] = useState("");
  const [lineW, setLineW] = useState(5);
  const [prevCoords, setPrevCoords] = useState({ prevX: null, prevY: null });
  const [isDrawing, setIsDrawing] = useState(false);

  useEffect(() => {
    const canvas = canvasRef.current;
    const body = bodyRef.current;

    if (canvas) {
      canvas.height = window.innerHeight;
      canvas.width = window.innerWidth;

      const ctx = canvas.getContext("2d");
      ctx.lineWidth = lineW;


    const handleColorChange = () => {
      setTheColor(theInputRef.current.value);
      body.style.backgroundColor = theInputRef.current.value;
    };

    theInputRef.current.addEventListener("input", handleColorChange, false);

    const handleLineWidthChange = () => {
      setIsDrawing(false);
      const inputVal = ageInputRef.current.value;
      setLineW(inputVal);
      ageOutputRef.current.innerHTML = inputVal;
      ctx.lineWidth = inputVal;
    };

    ageInputRef.current.oninput = handleLineWidthChange;

    const handleClrClick = (e) => {
      ctx.strokeStyle = e.target.dataset.clr;
    };

    const clrs = Array.from(document.querySelectorAll(".clr"));
    clrs.forEach((clr) => {
      clr.addEventListener("click", handleClrClick);
    });

    const handleClearClick = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    };

    const handleSaveClick = () => {
      const data = canvas.toDataURL("image/png");
      const a = document.createElement("a");
      a.href = data;
      a.download = "sketch.png";
      a.click();
    };

    const handleMouseDown = () => {
      setIsDrawing(true);

    };

    const handleMouseUp = () => {
      setIsDrawing(false);
    };

    const handleMouseMove = (e) => {
      const { prevX, prevY } = prevCoords;
      const currentX = e.clientX;
      const currentY = e.clientY;

      if (prevX === null || prevY === null || !isDrawing) {
        setPrevCoords({ prevX: currentX, prevY: currentY });
        return;
      }

      ctx.beginPath();
      ctx.moveTo(prevX, prevY);
      ctx.lineTo(currentX, currentY);
      ctx.stroke();

      setPrevCoords({ prevX: currentX, prevY: currentY });
    };

    canvas.addEventListener("mousedown", handleMouseDown);
    window.addEventListener("mouseup", handleMouseUp);
    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      theInputRef.current.removeEventListener("input", handleColorChange);
      clrs.forEach((clr) => {
        clr.removeEventListener("click", handleClrClick);
      });
      canvas.removeEventListener("mousedown", handleMouseDown);
      window.removeEventListener("mouseup", handleMouseUp);
      window.removeEventListener("mousemove", handleMouseMove);
    }
  }

  } , [canvas, lineW]);

  return (
    <div className="App">
      <div className="nav">
        <canvas ref={canvasRef} id="canvas"></canvas>
        <div className="clr" data-clr="#000"></div>
        <div className="clr" data-clr="red"></div>
        <div className="clr" data-clr="orange"></div>
      </div>
    </div>
  );
}


export default Netart
